

	var   log = require('ee-log')
		, LeakyBucket = require('./');


	var bucket = new LeakyBucket(60, 60, 10);



	var i = 100;

	while(i--) {
		(function(index){
			bucket.throttle().then(function() {
				log.info(index);
			}).catch(function(err) {
				log.error(index, err.message);
			});
		})(i);
	}